CREATE PROCEDURE [dbo].[FetchUSSDTransaction]
	@Id Bigint 
AS

Begin Insert into [dbo].[MenuTransactions] ( USSDMenuId ,USSDCampaignId ,USSDTransactionId ,MobileNetworkID ,MobileNumber ,EnteredSelectionValue ,MenuSection ,CreateDate ) Values ( @USSDMenuId ,@USSDCampaignId ,@USSDTransactionId ,@MobileNetworkID ,@MobileNumber ,@EnteredSelectionValue ,@MenuSection ,@CreateDate
)
Set @Id = SCOPE_IDENTITY() End

Begin Insert into [dbo].[USSDTransactions] ( [USSDCampaignId] ,[Message] ,[MobileNumber] ,[CreatedDate] ,[CreatedByUserId] ,[USSDNumberId] ,[SessionGuid] ,[MobileNetworkId] ) Values ( @USSDCampaignId ,@Message ,@MobileNumber ,@CreatedDate ,@CreatedByUserId ,@USSDNumberId ,@SessionGuid ,@MobileNetworkId
)
Set @Id = SCOPE_IDENTITY() End

